import styles from "./Flag.module.css";
import { Reactslider } from "./ReactSlider";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Flag = () => {
  let team1 = [
    {
      img: "data:image/png;base64,",
      name: "India",
    },
    {
      img: "data:image/png;base64,",
      name: "New Zealand",
    },
    {
      img: "data:image/jpeg;base64,",
      name: "West Indies",
    },
    {
      img: "data:image/png;base64,",
      name: "Autralia",
    },
  ];

  let team2 = [
    {
      img: "data:image/png;base64,",
      name: "Pakistan",
    },
    {
      img: "data:image/png;base64,",
      name: "England",
    },
    {
      img: "data:image/png;base64,",
      name: "Srilanka",
    },
    {
      img: "data:image/jpeg;base64,",
      name: "South Africa",
    },
  ];
  const [team, setTeam] = useState("");
  const [teamTwo, setTeam2] = useState("");
  const navigate = useNavigate();
  let x = localStorage.getItem("team1");
  console.log(x);
  const handleClick = (ele) => {
    // console.log(ele.name);
    // setTeam([...team, ele.name]);
    setTeam(ele.name);
    localStorage.setItem("team1", JSON.stringify(team));
    console.log(team);
  };

  const handleClick2 = (ele) => {
    // console.log(ele.name);
    // setTeam([...team, ele.name]);
    localStorage.setItem("team2", JSON.stringify(teamTwo));
    setTeam2(ele.name);
  };

  const handleClick3 = () => {
    navigate("/scoreboard");
  };

  return (
    <>
      <div className={styles.FlagBox}>
        <Reactslider
          Team={team1}
          local="team1"
          team={team}
          handleClick={handleClick}
        />
        <Reactslider
          Team={team2}
          local="team2"
          team={teamTwo}
          handleClick={handleClick2}
        />
      </div>
      {team && teamTwo != "" ? (
        <div
          style={{
            width: "600px",
            display: "flex",
            justifyContent: "space-between",
            margin: "auto",
          }}
        >
          <h1>{team}</h1>
          <h3>vs</h3>
          <h1>{teamTwo}</h1>
          <button
            onClick={handleClick3}
            style={{ height: "30px", marginTop: "30px" }}
          >
            Select
          </button>
        </div>
      ) : (
        ""
      )}
    </>
  );
};

export { Flag };
